export default {
	myVar2: {},
	fetchGeneresReport: () => {
		//write code here
		try {
			const myData = getMovieGenres.data.reduce((acc, item, index) => {
				if (Array.isArray(item.genres)) {
					item.genres.forEach((genre) => {
						let val = typeof(genre) === "object" ? genre?.name : genre;
						if (val) {
							if (acc[val]) {
							acc[val] += 1;
							} else {
								acc[val] = 1;
							}
						}

					});
				}
				return acc;	
			}, {});
			return Object.entries(myData).map(([x, y]) => ({x, y}));
		} catch(error) {
			console.error(error);
		}
	},
	formatAmount: (amount) => {
		//use async-await or promises
		return `$ ${new Intl.NumberFormat().format(amount)}`
	},
	getRevenues: () => {
		const quaters = [
			{
				x: "Jan-Mar",
				y: 0,
			},
			{
				x: "Apr-Jun",
				y: 0,
			},
			{
				x: "Jul-Sep",
				y: 0,
			},
			{
				x: "Oct-Dec",
				y: 0,
			},
		];
		getMovies.data.forEach((item) => {
			let val = moment(item.release_date).quarter();
			if (val) {
				quaters[val-1].y += item.revenue;
			}

		});
		return quaters;
	},
}
